mapper.update:
update plan
set course_id = #{courseId,jdbcType=INTEGER},
  term = #{term,jdbcType=INTEGER},
  teacher_id = #{teacherId,jdbcType=INTEGER}
where plan_id = #{planId,jdbcType=INTEGER}

mapper.select:
select plan_id, course_id, term, teacher_id
from plan
where plan_id = #{planId,jdbcType=INTEGER}
